// src/components/NoticeBoard.js
import React, { useState } from 'react';
import './NoticeBoard.css';

const noticeData = [
    {
        id: 1,
        title: '25년 2월 17일 단축 영업 안내',
        writer: '관리자',
        date: '2025.07.17 13:28',
    },
    {
        id: 2,
        title: '마이니모에 정식오픈 안내',
        writer: '관리자',
        date: '2024.12.06 17:30',
    },
    {
        id: 3,
        title: '마포점 영업시간 변경안내 (11/6부터)',
        writer: '관리자',
        date: '2023.11.02 12:08',
    },
    // 필요한 만큼 추가...
];

function NoticeBoard() {
    const [keyword, setKeyword] = useState('');

    const filtered = noticeData.filter((notice) =>
        notice.title.includes(keyword)
    );

    return (
        <div className="notice-wrapper">
            <h2>공지사항</h2>
            <table className="notice-table">
                <thead>
                <tr>
                    <th>번호</th>
                    <th>제목</th>
                    <th>글쓴이</th>
                    <th>등록일</th>
                </tr>
                </thead>
                <tbody>
                {filtered.map((notice, index) => (
                    <tr key={notice.id}>
                        <td>공지</td>
                        <td>{notice.title}</td>
                        <td>{notice.writer}</td>
                        <td>{notice.date}</td>
                    </tr>
                ))}
                </tbody>
            </table>

            <div className="notice-search">
                <input
                    type="text"
                    placeholder="검색"
                    value={keyword}
                    onChange={(e) => setKeyword(e.target.value)}
                />
                <button>검색</button>
            </div>
        </div>
    );
}

export default NoticeBoard;
