// Navbar.js
import React, { useState, useEffect } from 'react';
import './Navbar.css';

function Navbar({ onNavigate }) {
    const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

    useEffect(() => {
        const handleResize = () => {
            setIsMobile(window.innerWidth <= 768);
        };
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    return (
        <nav className={`navbar ${isMobile ? 'mobile' : ''}`}>
            <div className="nav-logo">miny moe.</div>

            <ul className={`nav-menu ${isMobile ? 'mobile-menu open' : 'desktop-menu'}`}>
                <li onClick={() => onNavigate('home')}>HOME</li>
                {/*<li>STORE</li>*/}
                <li onClick={() => onNavigate('dessert')}>DESSERT</li>
                {/*<li>SHOP</li>*/}
                <li>
                    <a
                        href="https://www.instagram.com/miny_moe_jeju/"
                        target="_blank"
                        rel="noopener noreferrer"
                    >
                        SNS
                    </a>
                </li>
                <li onClick={() => onNavigate('notice')}>NOTICE</li>
            </ul>
        </nav>
    );
}

export default Navbar;
