// Navbar.js
import React, { useState, useEffect } from 'react';
import './Navbar.css';

function Navbar({ onNavigate }) {
    const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
    const [menuOpen, setMenuOpen] = useState(false);

    useEffect(() => {
        const handleResize = () => {
            const mobile = window.innerWidth <= 768;
            setIsMobile(mobile);
            if (!mobile) setMenuOpen(false); // 데스크탑일 땐 메뉴 닫기
        };
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const handleNavigate = (target) => {
        onNavigate(target);
        setMenuOpen(false); // 메뉴 닫기
    };

    return (
        <>
            <nav className="navbar">
                {isMobile && (
                    <button className="hamburger" onClick={() => setMenuOpen(!menuOpen)}>
                        ☰
                    </button>
                )}
                <div className="nav-logo">miny moe.</div>

                {!isMobile && (
                    <ul className="nav-menu desktop-menu">
                        <li onClick={() => handleNavigate('home')}>HOME</li>
                        <li onClick={() => handleNavigate('dessert')}>DESSERT</li>
                        <li>
                            <a
                                href="https://www.instagram.com/miny_moe_jeju/"
                                target="_blank"
                                rel="noopener noreferrer"
                            >
                                SNS
                            </a>
                        </li>
                        <li onClick={() => handleNavigate('notice')}>NOTICE</li>
                    </ul>
                )}
            </nav>

            {/* ✅ 모바일 슬라이드 메뉴 */}
            {isMobile && (
                <>
                    <div className={`mobile-menu ${menuOpen ? 'open' : ''}`}>
                        <ul className="nav-menu">
                            <li onClick={() => handleNavigate('home')}>HOME</li>
                            <li onClick={() => handleNavigate('dessert')}>DESSERT</li>
                            <li>
                                <a
                                    href="https://www.instagram.com/miny_moe_jeju/"
                                    target="_blank"
                                    rel="noopener noreferrer"
                                >
                                    SNS
                                </a>
                            </li>
                            <li onClick={() => handleNavigate('notice')}>NOTICE</li>
                        </ul>
                    </div>
                    {menuOpen && <div className="menu-overlay" onClick={() => setMenuOpen(false)} />}
                </>
            )}
        </>
    );
}

export default Navbar;
